from fpdf import FPDF
from datetime import datetime
import os
import platform

def clear_screen():
    if platform.system() == "Windows":
        os.system('cls')  # Windows
    else:
        os.system('clear')  # macOS, Linux

def pause():
    input("Press Enter to continue...")

def kysy_kokonaishinta():
    while True:
        try:
            hinta = float(input("Mikä on ostettavan tuotteen/palvelun kokonaishinta (alv 0 %)? "))
            if hinta >= 0:
                return hinta
            else:
                print("Hinnan täytyy olla positiivinen luku.")
        except ValueError:
            print("Virheellinen syöte. Anna hinta numerona.")

def kysy_3v_hinta(hinta):
    while True:
        try:
            hinta_3v = float(input(f"Mikä on palvelun hinta 3 vuoden ajalta (alv 0 %)? (vähintään {hinta}) "))
            if hinta_3v >= hinta:
                return hinta_3v
            else:
                print(f"Hinnan täytyy olla vähintään {hinta}.")
        except ValueError:
            print("Virheellinen syöte. Anna hinta numerona.")

def kysy_tulevat_hankinnat():
    while True:
        vastaus = input("Onko todennäköistä, että kyseisestä liikkeestä hankitaan uudelleen tavaraa/palvelua? (kyllä/ei) ").lower()
        if vastaus in ['kyllä', 'ei']:
            if vastaus == 'kyllä':
                while True:
                    try:
                        arvio_3v = float(input("Mikä on arvio, että 3 vuoden aikana tavaraa/palvelua hankittaisiin summalla (alv 0 %)? "))
                        if arvio_3v >= 0:
                            return arvio_3v
                        else:
                            print("Arvion täytyy olla positiivinen luku.")
                    except ValueError:
                        print("Virheellinen syöte. Anna arvio numerona.")
            else:
                return 0
        else:
            print("Vastaa kyllä tai ei.")

def kysy_hankintalaji():
    print("Valitse hankintalaji:")
    print("A. Tavarahankinnat/Palveluhankinnat/Suunnittelukilpailut/Rakennusurakoihin liittyvät suunnittelu ja konsulttipalvelut")
    print("B. Rakennusurakat")
    print("C. Muut erityiset palveluhankinnat (Hankintalain Liitteessä E määritellyt palvelut)")
    print("D. Sosiaali- ja terveyspalvelut (Hankintalain Liitteessä E määritellyt palvelut)")
    print("E. Käyttöoikeussopimukset")

    while True:
        valinta = input("Valintasi: ").upper()
        if valinta in ['A', 'B', 'C', 'D', 'E']:
            return valinta
        else:
            print("Virheellinen valinta, yritä uudelleen.")

def kysy_puitesopimus():
    while True:
        vastaus = input("Onko hankinnalle olemassa olevaa puitesopimusta tai kilpailutusta? (kyllä/ei) ").lower()
        if vastaus in ['kyllä', 'ei']:
            return vastaus == 'kyllä'
        else:
            print("Vastaa kyllä tai ei.")

